import React, { useEffect, useState } from 'react';
import axios from 'axios';


let genre;
let randomYear;
let keyword1;
let keyword2;
let keyword3;
let keyword4;
let keyword5;

let season;
let month;

let currentDate = new Date(); // variable to store date

// ARRAYS

// --- ANGRY Keywords ---
// fight 1721, martial arts 779, spy 470, tension 10481, warrior 192913, battle 14643, mercenary 3070, revenge 9748
const angryKeywords = ["1721","779","470","10481","192913","14643","3070","9748"];
// --- HAPPY Keywords ---
//happy 231591, love 9673, sports documentary 159290, nature documentary 221355, happiness 154060, family 18035, magic 2343
const happyKeywords = ["231591","9673","159290","221355","154060","18035","2343"];

// entertainer 1491, happy 231591, comedy 248206
const tiredKeywords = ["1491","231591","248206"];

// --- SPRING Keywords ---
// spring break 158054, game 18249, party 8508, young adult 206298
const springKeywords = ["158054","18249","8508","206298"];

// --- SUMMER Keywords ---
// summer 13088, summer camp 5767, beach 966, resort 10793, family secrets, teenage girl, family relationships
const summerKeywords = ["13088","5767","966","10793","6625","10809","10235"];

// --- WINTER Keywords ---
// winter 1442, snow 10794, christmas 207317, christmas party 1441, christmas horror 186466
const winterKeywords = ["1442","10794","207317","186466"];

// --- AUTUMN Keywords ---
// autumn 178883, halloween 3335, halloween party 9694, horror 8087, monster 1299, vampire 3133, supernatural 6152
const autumnKeywords = ["178883","3335","9694","8087","1299","3133","6152"];

// RANDOM Keywords Holder
// Array to store random keywords
// Used to merge two arrays together
let mergedKeywordsHolder = []; 

// Selected ramdom number from mergedKeywordsHolder array
let selectedKeywords = [];

// -------




// FUNCTION TO FILTER ANGRY MOOD MOVIES
function filterMovieKeywords() {
    // loop to get 6 random values from array angryKeywords and store it in the array randomKeywordsHolder
    for (var i = 0; i < 5; i++) { 
      var pickedKeyword = mergedKeywordsHolder[Math.floor(Math.random()*mergedKeywordsHolder.length)]; // store the random value from the array angryKeywords into the variable pickedKeyword
      while (selectedKeywords.lastIndexOf(pickedKeyword) !== -1) { // check if the last value is not the same as the new one
        pickedKeyword = mergedKeywordsHolder[Math.floor(Math.random()*mergedKeywordsHolder.length)];
      }
      selectedKeywords.push(pickedKeyword); // adds the random value at the end of the array randomKeywordsHolder
  }
    // assign keywords from randomKeywordsHolder array to variable keyword1, keyword2 etc
    keyword1 = selectedKeywords[0];
    keyword2 = selectedKeywords[1];
    keyword3 = selectedKeywords[2];
    keyword4 = selectedKeywords[3];
    keyword5 = selectedKeywords[4];

    // Generate random year between 2010 and 2020
  randomYear = getRndInteger(2010, 2020);
  console.log("selected keys ", selectedKeywords)
   console.log("keywords ",keyword1, keyword2, keyword3, keyword4,keyword5)
  }

  // FUNCTION TO GENERATE RANDOM YEAR
  function getRndInteger(min, max) {
    return Math.floor(Math.random() * (max - min)) + min;
  }

// GET CURRENT MONTH
let months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
month = months[currentDate.getMonth()];
console.log("Month: " + month);

// GET THE RIGHT SEASON
function getSeason() {
  if (month === "December" || month === "January" || month === "February") {
    //console.log("Winter");
    season = "Winter";
  }
  else if (month === "March" || month === "April" || month === "May") {
    //console.log("Spring");
    season = "Spring";
  }
  else if (month === "June" || month === "July" || month === "August") {
    //console.log("Summer");
    season = "Summer";
  }
  else {
    //console.log("Autumn");
    season = "Autumn";
  }
  return season;
}


// HAPPY USER MOOD OPTIONS
const tiredMoodTest = () => {
    genre = "35|10770|53";
    // CHECK IF IT IS SPRING
      if (getSeason() === "Spring") {
        console.log("It's Spring");
        mergedKeywordsHolder = []; // clear the array
        selectedKeywords = []; // clear the array
        mergedKeywordsHolder = tiredKeywords.concat(springKeywords); // merge two arrays together into a new one called mergedKeywordsHolder
      // Call this fucntion to filter movies
        filterMovieKeywords();
      }
      else if (getSeason() === "Summer") {
        console.log("It's Summer");
        mergedKeywordsHolder = []; // clear the array
        selectedKeywords = []; // clear the array
        mergedKeywordsHolder = tiredKeywords.concat(summerKeywords); // merge two arrays together into a new one called mergedKeywordsHolder
      // Call this fucntion to filter movies
        filterMovieKeywords();
      }
      else if (getSeason() === "Winter") {
        console.log("It's winter");
        mergedKeywordsHolder = []; // clear the array
        selectedKeywords = []; // clear the array
        mergedKeywordsHolder = tiredKeywords.concat(winterKeywords); // merge two arrays together into a new one called mergedKeywordsHolder
        // Call this fucntion
        filterMovieKeywords();
      }
      else if (getSeason() === "Autumn") {
        console.log("It's Autumn");
        mergedKeywordsHolder = []; // clear the array
        selectedKeywords = []; // clear the array
        mergedKeywordsHolder = tiredKeywords.concat(autumnKeywords); // merge two arrays together into a new one called mergedKeywordsHolder
        // Call this fucntion to filter movies
        filterMovieKeywords();
      } 
  }



function RecommendedMovies({ genre, keyword1, keyword2, keyword3, keyword4, keyword5, randomYear}) {
    const [movies, setMovies] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [isError, setIsError] = useState(false);
    useEffect(() => {
        const getData = async() => {
            setIsError(false);
            setIsLoading(true);

            try {
                const result = await axios(`https://api.themoviedb.org/3/discover/movie?api_key=fb1090731e2fd7eea03536049722f122&language=en-GB&sort_by=popularity.desc&include_adult=false&include_video=false&primary_release_date.gte=${randomYear}-01-01&with_genres=${genre}&with_keywords=${keyword1}|${keyword2}|${keyword3}|${keyword4}|${keyword5}`);
                setMovies(result.data.results);
                console.log("List Data: ", result.data.results)
            }
            catch (isError) {
                setIsError(true);
            }
            setIsLoading(false);
        }
        getData();
    }, [genre]);

    return (
        <div className="popular-movies">
        {isError && <div>Something went wrong...</div>}

        {isLoading ? (
        <div>Loading...</div>
        ) : (<div className="movie-container"> {movies.map(movie => (
                <div className="movie-list">
                <img src={"ola" + movie.poster_path} key={movie.id} id={movie.id} alt={movie.title} />
                </div>
            ))}</div>
            )}

        </div>
    )

}

export default RecommendedMovies
