import React, { useEffect, useState } from 'react';
import Movie from './getMovie';
import Trailer from './getTrailer';
import './App.css';

function App() {

const API_KEY = "fb1090731e2fd7eea03536049722f122";
const IMG_URL = "https://image.tmdb.org/t/p/w500";
const RECOMMENDED_MOVIES_PATH = "/discover/movie";
const UPCOMING_PATH = "/movie/upcoming";
const NOWPLAYING_PATH = "/movie/now_playing";
const POPULAR_PATH = "/movie/popular";

const [upComingMovieList, setUpComingMovieList] = useState([]); // UP COMING MOVIE LIST
const [nowPlayingMovieList, setNowPlayingMovieList] = useState([]); // NOW PLAYING MOVIE LIST
const [popularMovieList, setPopularMovieList] = useState([]); // POPULAR MOVIE LIST
const [trailerList, setTrailerList] = useState([]); // TRAILER LIST
const [recommendedMovieList, setRecommendedMovieList] = useState([]); // RECOMMENDED MOVIE LIST

let genre = "878";
let randomYear = "2010";
let keyword1 = "14626";
let keyword2 = "10481";
let keyword3 = "178883";
let keyword4 = "9694";
let keyword5 = "3133";

// ARRAYS

// --- ANGRY Keywords ---
// fight 1721, martial arts 779, spy 470, tension 10481, warrior 192913, battle 14643, mercenary 3070, revenge 9748
const angryKeywords = ["1721","779","470","10481","192913","14643","3070","9748"];

// entertainer 1491, happy 231591, comedy 248206
const tiredKeywords = ["1491","231591","248206"];

// --- SPRING Keywords ---
// spring break 158054, game 18249, party 8508, young adult 206298
const springKeywords = ["158054","18249","8508","206298"];

// --- SUMMER Keywords ---
// summer 13088, summer camp 5767, beach 966, resort 10793
const summerKeywords = ["13088","5767","966","10793"];

// --- WINTER Keywords ---
// winter 1442, snow 10794, christmas 207317, christmas party 1441, christmas horror 186466
const winterKeywords = ["1442","10794","207317","186466"];

// --- AUTUMN Keywords ---
// autumn 178883, halloween 3335, halloween party 9694, horror 8087, monster 1299, vampire 3133, supernatural 6152
const autumnKeywords = ["178883","3335","9694","8087","1299","3133","6152"];

// RANDOM Keywords Holder
// Array to store random keywords
// Used to merge two arrays together
let mergedKeywordsHolder = []; 

// Selected ramdom number from mergedKeywordsHolder array
let selectedKeywords = [];

// -------


useEffect(() => {

  getUpComingMovieList()
  getPopularMovieList()
  getNowPlayingMovieList()

}, []);

// UP COMING MOVIE LIST
const getUpComingMovieList = async () => {
  const response = await fetch(`https://api.themoviedb.org/3${UPCOMING_PATH}?api_key=${API_KEY}&language=en-GB`);
  const data = await response.json();
  console.log("Movie List ", data.results);
  setUpComingMovieList(data.results);
}

// POPULAR MOVIE LIST
const getPopularMovieList = async () => {
  const response = await fetch(`https://api.themoviedb.org/3${POPULAR_PATH}?api_key=${API_KEY}&language=en-GB`);
  const data = await response.json();
  console.log("Movie List ", data.results);
  setPopularMovieList(data.results);
}

// POPULAR MOVIE LIST
const getNowPlayingMovieList = async () => {
  const response = await fetch(`https://api.themoviedb.org/3${NOWPLAYING_PATH}?api_key=${API_KEY}&language=en-GB`);
  const data = await response.json();
  console.log("Movie List ", data.results);
  setNowPlayingMovieList(data.results);
}


// USER MOOD OPTIONS
const happyMood = async () => {
  //PATH = "/movie/popular";
  const response = await fetch(`https://api.themoviedb.org/3${RECOMMENDED_MOVIES_PATH}?api_key=${API_KEY}&language=en-GB&sort_by=popularity.desc&include_adult=false&include_video=false&primary_release_date.gte=${randomYear}-01-01&with_genres=${genre}&with_keywords=${keyword1}`);
  const data = await response.json();
  console.log("Movie List Recommended: ", data.results);
  setRecommendedMovieList(data.results);
}

// USER MOOD OPTIONS
const angryMood = async () => {
  //PATH = "/movie/popular";
  const response = await fetch(`https://api.themoviedb.org/3${RECOMMENDED_MOVIES_PATH}?api_key=${API_KEY}&language=en-GB&sort_by=popularity.desc&include_adult=false&include_video=false&primary_release_date.gte=${randomYear}-01-01&with_genres=${genre}&with_keywords=${keyword1}`);
  const data = await response.json();
  console.log("Movie List Recommended: ", data.results);
  setRecommendedMovieList(data.results);
}


// GET VIDEO TRAILER
document.onclick = function (event) { // check for any event that happens in the page
  const target = event.target; // store the content from the "event" into the variable target
  if (target.tagName.toLowerCase() === "img") { // change the image name to lower case and check if it is equal to "img"
  console.log(target);
  const movie_id = target.attributes.id.value; // get movie ID
  console.log("Movie ID: ", movie_id);

  const getTrailer = async () => {
    const TRAILER_PATH = `/movie/${movie_id}/videos`; // create url to search for movie id
    const response = await fetch(`https://api.themoviedb.org/3${TRAILER_PATH}?api_key=${API_KEY}&language=en-GB`);
    const data = await response.json();
    console.log("Trailer List: ", data.results);
    setTrailerList(data.results);

  // check if there is a trailer available
  if (data.results.length === 0) {
    console.log("No trailer found");

  return;
  }

  }
  getTrailer();


  }
}





return (
  <div className="App">

    <h1 className="logo"><a href="index.html">fastMovie</a></h1>

    <div className="mood-options">
      <p className="mood-status">How Are You Feeling Today?</p>
      <button className="btn-happy" onClick={() => happyMood()}>Happy</button>
      <button className="btn-sad" onClick={() => getUpComingMovieList("/movie/popular")}>Sad</button>
      <button>Tired</button>
      <button className="btn-angry" onClick={() => angryMood()}>Angry</button>
    </div>

    <h3>Recommended For You</h3>
    <div className="recommended-movies-container">
      {recommendedMovieList.map(movie => (
        <Movie 
        key= {movie.id}
        movie_id= {movie.id}
        poster= {IMG_URL + movie.poster_path} 
        rating= {movie.vote_average} />
      ))}
      </div>

      <h3>Up Coming</h3>
      <div className="movie-container">
      {upComingMovieList.map(movie => (
        <Movie 
        key= {movie.id}
        movie_id= {movie.id}
        poster= {IMG_URL + movie.poster_path} 
        rating= {movie.vote_average} />
      ))}
      </div>

      <h3>Popular</h3>
      <div className="movie-container">
      {popularMovieList.map(movie => (
        <Movie 
        key= {movie.id}
        movie_id= {movie.id}
        poster= {IMG_URL + movie.poster_path} 
        rating= {movie.vote_average} />
      ))}
      </div>

      <h3>Now Playing</h3>
      <div className="movie-container">
      {nowPlayingMovieList.map(movie => (
        <Movie 
        key= {movie.id}
        movie_id= {movie.id}
        poster= {IMG_URL + movie.poster_path} 
        rating= {movie.vote_average} />
      ))}
      </div>

      <div className="trailer-container">       
      {trailerList.slice(0, 1).map(trailer => (
        <Trailer
        key= {trailer.key}
        trailer_key= {trailer.key}
      />
      ))}
      </div>


  </div>
  );
}

export default App;
