import React, { useEffect, useReducer } from 'react';
import axios from 'axios';

const initialState = {
    loading: true,
    error: "",
    movie: {}
}

let keyword1 = "5767";
let randomYear = "2010";
let genre = "28";

const reducer = (state, action) => {
    switch(action.type) {
        case "Fecth_Success":
            return {
                loading: false,
                movie: action.payload,
                error: ""
            }
            case "Fetch_Error": 
            return {
                loading: false,
                movie: {},
                error: "Something went wrong"
            }
            default:
            return state
    }
}

function Mood() {

        const [state, dispatch] = useReducer(reducer, initialState)

    useEffect(() => {
        axios
        .get(`https://api.themoviedb.org/3/discover/movie?api_key=fb1090731e2fd7eea03536049722f122&language=en-GB&sort_by=popularity.desc&include_adult=false&include_video=false&primary_release_date.gte=${randomYear}-01-01&with_genres=${genre}&with_keywords=${keyword1}`)
        .then(response => {
            dispatch({type: "Fetch_Success", payload: response.data})
            console.log(response.data);

        })
        .catch(error => {
            dispatch({type: "Fetch_Error"})
        })
    }, []);

    return (
        <div className="recommended-movies">
        {state.error && <div>Something went wrong...</div>}

        {state.loading ? (
        <div>Loading...</div>
        ) : (<div className="recommended-movies-container"> {state.movie.map(movie => (
                <div className="movie-list" key={movie.id}>
                <img src={"https://image.tmdb.org/t/p/w500" + movie.poster_path} id={movie.id} alt={movie.title} />
                <button className="btn-play"></button>
                </div>
            ))}</div>
            )}

        </div>
    )

}

export default Mood
