        const getData = async() => {
            setIsError(false);
            setIsLoading(true);

            try {
                const result = await axios(`https://api.themoviedb.org/3/discover/movie?api_key=fb1090731e2fd7eea03536049722f122&language=en-GB&sort_by=popularity.desc&include_adult=false&include_video=false&primary_release_date.gte=${randomYear}-01-01&with_genres=${genre}&with_keywords=${keyword1}|${keyword2}|${keyword3}|${keyword4}|${keyword5}`);
                setMovies(result.data.results);
                console.log("List Data: ", result.data.results)
                console.log("List Data CEHCEK: ", result, genre, randomYear);

            }
            catch (isError) {
                setIsError(true);
            }
            setIsLoading(false);
        }
        getData();
    }, [userMood]);

    return (
        <div className="recommended-movies">
        {isError && <div>Something went wrong...</div>}

        {isLoading ? (
        <div>Loading...</div>
        ) : (<div className="recommended-movies-container"> {movies.map(movie => (
                <div className="movie-list" key={movie.id}>
                <img src={"https://image.tmdb.org/t/p/w500" + movie.poster_path} id={movie.id} alt={movie.title} />
                <button className="btn-play"></button>
                </div>
            ))}</div>
            )}

        </div>
    )

}

