import React, { useEffect, useState } from 'react';
import Trailer from './components/MovieTrailer';
import Modal from 'react-modal';
import MovieList from './components/MovieList';

import './App.css';

Modal.setAppElement('#root');

function App() {

  const [trailerList, setTrailerList] = useState([]); // TRAILER LIST
  const [showOverlay, setShowOverlay] = useState("overlay");
  const [modalIsOpen, setmodalIsOpen] = useState(false);
  const [isUserMoodActive, setIsUserMoodActive] = useState(false);
  const [recommendedMovieList, setRecommendedMovieList] = useState([]);
  const [moodBasedMovieList, setMoodBasedMovieList] = useState([]);
  const [seasonBasedMovieList, setSeasonBasedMovieList] = useState([]);


  const customStyles = {
    content: {
      top: '35%',
      left: '50%',
      right: 'auto',
      bottom: 'auto',
      marginRight: '-50%',
      width: '40%',
      transform: 'translate(-50%, -10%)',
      textAlign: 'center',
    },
    overlay: {
      backgroundColor: 'rgb(0, 0, 0, .5)',
    },
  };

  return (
    <div className="App">
      <div className={showOverlay} onClick={() => { setShowOverlay("overlay"); setTrailerList([]); }}></div>

      <div className="NavigationBar">
        <h1 className="logo"> </h1>

      </div>


      {isUserMoodActive &&
        <h3>Recommended For You</h3>}
      <div className="recommended-movies-container">
        {recommendedMovieList.map(movie => (
          <RecommendedMovies
            key={movie.id}
            title={movie.title}
            poster={IMG_URL_LQ + movie.poster_path}
            movie_id={movie.id}
          />
        ))}
      </div>

      <h3>Up Coming</h3>
      <MovieList URL="/movie/upcoming" />

      <h3>Mood Based</h3>
      <div className="recommended-movies-container">
        {moodBasedMovieList.map(movie => (
          <RecommendedMovies
            key={movie.id}
            title={movie.title}
            poster={IMG_URL_LQ + movie.poster_path}
            movie_id={movie.id}
          />
        ))}
      </div>

      <h3>Now Playing</h3>
      <MovieList URL="/movie/now_playing" />

      <h3>Season Based Movies</h3>
      <div className="recommended-movies-container">
        {seasonBasedMovieList.map(movie => (
          <RecommendedMovies
            key={movie.id}
            title={movie.title}
            poster={IMG_URL_LQ + movie.poster_path}
            movie_id={movie.id}
          />
        ))}
      </div>

      <h3>Popular Movies</h3>
      <MovieList URL="/movie/popular" />


      <div className="trailer-container">
        {trailerList.slice(0, 1).map(trailer => (
          <Trailer
            key={trailer.key}
            trailer_key={trailer.key}
          />
        ))}
      </div>

      <Modal isOpen={modalIsOpen} onRequestClose={() => { setmodalIsOpen(false); setShowOverlay("overlay"); }} style={customStyles}>
        <p>SORRY, NO TRAILER AVAILABLE FOR THIS MOVIE</p>
      </Modal>

    </div>
  );
}


export default App;
