import React, { useEffect, useState } from 'react';
import axios from 'axios';

const API_KEY = "fb1090731e2fd7eea03536049722f122";
const IMG_URL_LQ = "https://image.tmdb.org/t/p/w500";

function RecommendedMovies({URL}) {
    const [movies, setMovies] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [isError, setIsError] = useState(false);


    useEffect(() => {
        const fetchData = async() => {
            setIsError(false);
            setIsLoading(true);

            try {
                const result = await axios(`https://api.themoviedb.org/3${URL}?api_key=${API_KEY}&language=en-GB`);
                setMovies(result.data.results);
                console.log("List Data REC: ", result.data.results)
            }
            catch (isError) {
                setIsError(true);
            }
            setIsLoading(false);
        }
        fetchData();
    }, [URL]);

    return (
        <div className="recommended-movies">
            {movies.map(movie => (
                <div className="movie-list" key={movie.id}>
                <img src={IMG_URL_LQ + movie.poster_path} id={movie.id} alt={movie.title} />
                </div>
            ))}     

        </div>
    )

}

export default RecommendedMovies
