import React, { useEffect, useState, useReducer } from 'react';
import axios from 'axios';

const initialState = {
    Loading: true,
    Error: "",
    movie: {}
}

const reducer = (state, action) => {
    switch(action.type) {
        case "fecth_Success":
            return {
                Loading: false,
                movie: action.payload,
                error: ""
            }
    }
}

function MoodBasedOnly({ genre, randomYear, moodkeyword1, moodkeyword2, moodkeyword3, moodkeyword4, moodkeyword5}) {
    const [movies, setMovies] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [isError, setIsError] = useState(false);
    useEffect(() => {
        const getData = async() => {
            setIsError(false);
            setIsLoading(true);

            try {
                const result = await axios(`https://api.themoviedb.org/3/discover/movie?api_key=fb1090731e2fd7eea03536049722f122&language=en-GB&sort_by=popularity.desc&include_adult=false&include_video=false&primary_release_date.gte=${randomYear}-01-01&with_genres=${genre}&with_keywords=${moodkeyword1}|${moodkeyword2}|${moodkeyword3}|${moodkeyword4}|${moodkeyword5}`);
                setMovies(result.data.results);
                console.log("List Data: ", result.data.results)
                console.log("List Data CEHCEK: ", result, genre, randomYear);

            }
            catch (isError) {
                setIsError(true);
            }
            setIsLoading(false);
        }
        getData();
    }, [moodkeyword1, moodkeyword2, moodkeyword3, moodkeyword4, moodkeyword5]);

    return (
        <div className="recommended-movies">
        {isError && <div>Something went wrong...</div>}

        {isLoading ? (
        <div>Loading...</div>
        ) : (<div className="recommended-movies-container"> {movies.map(movie => (
                <div className="movie-list" key={movie.id}>
                <img src={"https://image.tmdb.org/t/p/w500" + movie.poster_path} id={movie.id} alt={movie.title} />
                <button className="btn-play"></button>
                </div>
            ))}</div>
            )}

        </div>
    )

}

export default MoodBasedOnly
