    season = "Winter";
  }
  else if (month === "March" || month === "April" || month === "May") {
    //console.log("Spring");
    season = "Spring";
  }
  else if (month === "June" || month === "July" || month === "August") {
    //console.log("Summer");
    season = "Summer";
  }
  else {
    //console.log("Autumn");
    season = "Autumn";
  }
  return season;
}

return (
  <div className="App">
        <div className={showOverlay} onClick={() => {setShowOverlay("overlay"); setTrailerList([]);}}></div>

        <div className="spotlight">
        <h1 className="logo">

        <div className="mood-options">
        <a className="logo-link" href="index.html">fastMovie</a>
        <button className="btn-angry" onClick={() => {angryMood();  setIsUserMoodActive(true) }}>Angry</button>
        <button className="btn-tired" onClick={() => {tiredMood();  setIsUserMoodActive(true) }}>Tired</button>
        <button className="btn-sad" onClick={() => happyMood()}>Sad</button>
        <button className="btn-happy" onClick={() => {happyMood();  setIsUserMoodActive(true) }}>Happy</button>
        </div>
        </h1>

        {//<img src={IMG_URL_HQ + recommendedMovieList[0].backdrop_path} id={recommendedMovieList[0].id} alt=""/>  
