      }

      <div className="ads">
      <img src={require('./img/smallads.gif')} alt="" />
      </div>

      <h3>Up Coming</h3>
    <MovieList URL_PATH="/movie/upcoming"/>


      <h3>Now Playing</h3>
    <MovieList URL_PATH="/movie/now_playing"/>


      <div className="ads">
      <img src={require('./img/bigads.jpg')} alt="" />
      </div>

      <h3>Popular Movies</h3>
      <MovieList URL_PATH="/movie/popular"/>


      <div className="trailer-container">    
      {trailerList.slice(0, 1).map(trailer => (
        <Trailer
        key= {trailer.key}
        trailer_key= {trailer.key}
      />
      ))}
      </div>

    <Modal isOpen={modalIsOpen} onRequestClose={() => {setmodalIsOpen(false); setShowOverlay("overlay");}} style={customStyles}>
