import React, { useEffect, useState } from 'react';
import axios from 'axios';


function RecommendedMovies({URL}) {
    const [recmovies, setRecMovies] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [isError, setIsError] = useState(false);

    useEffect(() => {
        const fetchData = async() => {
            setIsError(false);
            setIsLoading(true);

            try {
                const result = await axios(URL);
                setRecMovies(result.data.results);
                console.log("List Data REC: ", result.data.results)
            }
            catch (isError) {
                setIsError(true);
            }
            setIsLoading(false);
        }
        fetchData();
    }, [URL]);

    return (
        <div className="recommended-movies">
        {isError && <div>Something went wrong...</div>}

        {isLoading ? (
        <div>Loading...</div>
        ) : (<div className="recommended-movies-container"> {rmovies.map(movie => (
                <div className="movie-list" key={movie.id}>
                <img src={"https://image.tmdb.org/t/p/w500" + movie.poster_path} id={movie.id} alt={movie.title} />
                <button className="btn-play"></button>
                </div>
            ))}</div>
            )}

        </div>
    )

}

export default RecommendedMovies
