import React, { useEffect, useState, Component } from 'react';
import Movie from './getMovie';
import Trailer from './getTrailer';
import Modal from 'react-modal';
import './App.css';

Modal.setAppElement('#root');

function App() {

const API_KEY = "fb1090731e2fd7eea03536049722f122";
const IMG_URL_LQ = "https://image.tmdb.org/t/p/w1280";
const IMG_URL_HQ = "https://image.tmdb.org/t/p/w1280";
const RECOMMENDED_MOVIES_PATH = "/discover/movie";
const UPCOMING_PATH = "/movie/upcoming";
const NOWPLAYING_PATH = "/movie/now_playing";
const POPULAR_PATH = "/movie/popular";

const [upComingMovieList, setUpComingMovieList] = useState([]); // UP COMING MOVIE LIST
const [nowPlayingMovieList, setNowPlayingMovieList] = useState([]); // NOW PLAYING MOVIE LIST
const [popularMovieList, setPopularMovieList] = useState([]); // POPULAR MOVIE LIST
const [trailerList, setTrailerList] = useState([]); // TRAILER LIST
const [recommendedMovieList, setRecommendedMovieList] = useState([]); // RECOMMENDED MOVIE LIST
const [moodBasedMovieList, setMoodBasedMovieList] = useState([]); // RECOMMENDED MOVIE LIST


let genre;
let randomYear;
let keyword1;
let keyword2;
let keyword3;
let keyword4;
let keyword5;

// ARRAYS

// --- ANGRY Keywords ---
// fight 1721, martial arts 779, spy 470, tension 10481, warrior 192913, battle 14643, mercenary 3070, revenge 9748
const angryKeywords = ["1721","779","470","10481","192913","14643","3070","9748"];
// --- HAPPY Keywords ---
//happy 231591, love 9673, sports documentary 159290, nature documentary 
const happyKeywords = ["231591","9673","159290","221355","192913","14643","3070","9748"];

// entertainer 1491, happy 231591, comedy 248206
const tiredKeywords = ["1491","231591","248206"];

// --- SPRING Keywords ---
// spring break 158054, game 18249, party 8508, young adult 206298
const springKeywords = ["158054","18249","8508","206298"];

// --- SUMMER Keywords ---
// summer 13088, summer camp 5767, beach 966, resort 10793
const summerKeywords = ["13088","5767","966","10793"];

// --- WINTER Keywords ---
// winter 1442, snow 10794, christmas 207317, christmas party 1441, christmas horror 186466
const winterKeywords = ["1442","10794","207317","186466"];

// --- AUTUMN Keywords ---
// autumn 178883, halloween 3335, halloween party 9694, horror 8087, monster 1299, vampire 3133, supernatural 6152
const autumnKeywords = ["178883","3335","9694","8087","1299","3133","6152"];

// RANDOM Keywords Holder
// Array to store random keywords
// Used to merge two arrays together
let mergedKeywordsHolder = []; 

// Selected ramdom number from mergedKeywordsHolder array
let selectedKeywords = [];

// -------


useEffect(() => {
  happyMood()
  moodBased()
  getUpComingMovieList()
  getPopularMovieList()
  getNowPlayingMovieList()

}, []);



// UP COMING MOVIE LIST
const getUpComingMovieList = async () => {
  const response = await fetch(`https://api.themoviedb.org/3${UPCOMING_PATH}?api_key=${API_KEY}&language=en-GB&include_image_language=en,null`);
  const data = await response.json();
  console.log("Movie List ", data.results);
  setUpComingMovieList(data.results);
}

// POPULAR MOVIE LIST
const getPopularMovieList = async () => {
  const response = await fetch(`https://api.themoviedb.org/3${POPULAR_PATH}?api_key=${API_KEY}&language=en-GB&include_image_language=en,null`);
  const data = await response.json();
  console.log("Movie List ", data.results);
  setPopularMovieList(data.results);
}

// POPULAR MOVIE LIST
const getNowPlayingMovieList = async () => {
  const response = await fetch(`https://api.themoviedb.org/3${NOWPLAYING_PATH}?api_key=${API_KEY}&language=en-GB&include_image_language=en,null`);
  const data = await response.json();
  console.log("Movie List ", data.results);
  setNowPlayingMovieList(data.results);
}


// HAPPY USER MOOD OPTIONS
const happyMood = async () => {
  genre = "28|35|10759|12|18|9648";
  mergedKeywordsHolder = []; // clear the array
  selectedKeywords = []; // clear the array
  mergedKeywordsHolder = happyKeywords.concat(springKeywords); // merge two arrays together into a new one called mergedKeywordsHolder
  console.log(mergedKeywordsHolder);

  filterMovieKeywords()

  const response = await fetch(`https://api.themoviedb.org/3${RECOMMENDED_MOVIES_PATH}?api_key=${API_KEY}&language=en-GB&append_to_response=images&include_image_language=en,null&sort_by=popularity.desc&include_adult=false&include_video=false&primary_release_date.gte=${randomYear}-01-01&with_genres=${genre}&with_keywords=${keyword1}|${keyword2}|${keyword3}|${keyword4}|${keyword5}`);
  const data = await response.json();
  console.log("Movie List Happy Mood: ", data.results, "Year: ", randomYear);
  setRecommendedMovieList(data.results);
}

// ANGRY USER MOOD OPTIONS
const angryMood = async () => {
  genre = "28|80|10759|12";
  mergedKeywordsHolder = []; // clear the array
  selectedKeywords = []; // clear the array
  mergedKeywordsHolder = angryKeywords.concat(springKeywords); // merge two arrays together into a new one called mergedKeywordsHolder
  filterMovieKeywords()

  const response = await fetch(`https://api.themoviedb.org/3${RECOMMENDED_MOVIES_PATH}?api_key=${API_KEY}&language=en-GB&sort_by=popularity.desc&include_adult=false&include_video=false&primary_release_date.gte=${randomYear}-01-01&with_genres=${genre}&with_keywords=${keyword1}|${keyword2}|${keyword3}|${keyword4}|${keyword5}`);
  const data = await response.json();
  console.log("Movie List Angry Mood: ", data.results, "Year: ", randomYear);
  setRecommendedMovieList(data.results);

}

// USER MOOD OPTIONS
const moodBased = async () => {
  genre = "28|35|10759|12|18|9648";
  mergedKeywordsHolder = []; // clear the array
  selectedKeywords = []; // clear the array
  mergedKeywordsHolder = happyKeywords.concat(springKeywords); // merge two arrays together into a new one called mergedKeywordsHolder
  console.log(mergedKeywordsHolder);

  filterMovieKeywords()

  const response = await fetch(`https://api.themoviedb.org/3${RECOMMENDED_MOVIES_PATH}?api_key=${API_KEY}&language=en-GB&sort_by=popularity.desc&include_adult=false&include_video=false&primary_release_date.gte=${randomYear}-01-01&with_genres=${genre}&with_keywords=${keyword1}|${keyword2}|${keyword3}|${keyword4}|${keyword5}`);
  const data = await response.json();
  console.log("Movie List Happy Mood: ", data.results, "Year: ", randomYear);
  setMoodBasedMovieList(data.results);
}


// FUNCTION TO FILTER ANGRY MOOD MOVIES
function filterMovieKeywords() {
  // loop to get 6 random values from array angryKeywords and store it in the array randomKeywordsHolder
  for (var i = 0; i < 5; i++) { 
    var pickedKeyword = mergedKeywordsHolder[Math.floor(Math.random()*mergedKeywordsHolder.length)]; // store the random value from the array angryKeywords into the variable pickedKeyword
    while (selectedKeywords.lastIndexOf(pickedKeyword) !== -1) { // check if the last value is not the same as the new one
      pickedKeyword = mergedKeywordsHolder[Math.floor(Math.random()*mergedKeywordsHolder.length)];
    }
    selectedKeywords.push(pickedKeyword); // adds the random value at the end of the array randomKeywordsHolder
}
  // assign keywords from randomKeywordsHolder array to variable keyword1, keyword2 etc
  keyword1 = selectedKeywords[0];
  keyword2 = selectedKeywords[1];
  keyword3 = selectedKeywords[2];
  keyword4 = selectedKeywords[3];
  keyword5 = selectedKeywords[4];

  // Generate random year between 2010 and 2020
