import React, { useEffect, useState } from 'react';
import axios from 'axios';




function RecommendedMovies({API_KEY, genre, keyword1, keyword2, keyword3, keyword4, keyword5}) {
    const [movies, setMovies] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [isError, setIsError] = useState(false);
    useEffect(() => {
        const getData = async() => {
            setIsError(false);
            setIsLoading(true);

            try {
                const result = await axios(`https://api.themoviedb.org/3/discover/movie?api_key=${API_KEY}&language=en-GB&sort_by=popularity.desc&include_adult=false&include_video=false&primary_release_date.gte=${randomYear}-01-01&with_genres=${genre}&with_keywords=${keyword1}|${keyword2}|${keyword3}|${keyword4}|${keyword5}`);
                setMovies(result.data.results);
                console.log("List Data: ", result.data.results)
            }
            catch (isError) {
                setIsError(true);
            }
            setIsLoading(false);
        }
        getData();
    }, [genre]);

    return (
        <div className="popular-movies">
        {isError && <div>Something went wrong...</div>}

        {isLoading ? (
        <div>Loading...</div>
        ) : (<div className="movie-container"> {movies.map(movie => (
                <div className="movie-list">
                <img src={IMG_URL_LQ + movie.poster_path} key={movie.id} id={movie.id} alt={movie.title} />
                </div>
            ))}</div>
            )}

        </div>
    )

}

export default RecommendedMovies
