import React, { useEffect, useState } from 'react';
import Movie from './getMovie';
import Trailer from './getTrailer';
import './App.css';

function App() {

const API_KEY = "fb1090731e2fd7eea03536049722f122";
const IMG_URL = "https://image.tmdb.org/t/p/w500";
let PATH = "/movie/popular";

//const BASIC_PATH = `https://api.themoviedb.org/3${path}?api_key=fb1090731e2fd7eea03536049722f122&language=en-GB`;

//const [mood, setMood] = useState('');


// movie list
const [movieList, setMovieList] = useState([]);
// trailer list
const [trailerList, setTrailerList] = useState([]);

// movie list
const [recommendedMovieList, setRecommendedMovieList] = useState([]);



// USER PRE MADE CATEGORY 
const recommendedMovies = async () => {
  PATH = "/movie/upcoming";
  const response = await fetch(`https://api.themoviedb.org/3${PATH}?api_key=${API_KEY}&language=en-GB`);
  const data = await response.json();
  console.log("Movie List: ", data.results);
  setRecommendedMovieList(data.results);

}

// USER MOOD OPTIONS
const happyMood = async () => {
  //PATH = "/movie/popular";
  const response = await fetch(`https://api.themoviedb.org/3${PATH}?api_key=${API_KEY}&language=en-GB`);
  const data = await response.json();
  console.log("Movie List: ", data.results);
  setMovieList(data.results);
}


// GET VIDEO TRAILER
document.onclick = function (event) { // check for any event that happens in the page
  const target = event.target; // store the content from the "event" into the variable target
  if (target.tagName.toLowerCase() === "img") { // change the image name to lower case and check if it is equal to "img"
  console.log(target);
  const movie_id = target.attributes.id.value; // get movie ID
  console.log("Movie ID: ", movie_id);

  const getTrailer = async () => {
    const TRAILER_PATH = `/movie/${movie_id}/videos`; // create url to search for movie id
    const response = await fetch(`https://api.themoviedb.org/3${TRAILER_PATH}?api_key=${API_KEY}&language=en-GB`);
    const data = await response.json();
    console.log("Trailer List: ", data.results);
    setTrailerList(data.results);

  // check if there is a trailer available
  if (data.results.length === 0) {
    console.log("No trailer found");

  return;
  }

  }
  getTrailer();


  }
}





return (
  <div className="App">

    <h1 className="logo"><a href="index.html">fastMovie</a></h1>

    <div className="mood-options">
      <p className="mood-status">How Are You Feeling Today?</p>
      <button className="btn-happy" onClick={() => happyMood()}>Happy</button>
      <button className="btn-sad" onClick={() => recommendedMovies()}>Sad</button>
      <button>Tired</button>
      <button>Angry</button>
    </div>

    <div className="recommended-movies-container">
      {recommendedMovieList.map(movie => (
        <Movie 
        key= {movie.id}
        movie_id= {movie.id}
        poster= {IMG_URL + movie.poster_path} 
        rating= {movie.vote_average} />
      ))}
      </div>


      <div className="movie-container">
      {movieList.map(movie => (
        <Movie 
        key= {movie.id}
        movie_id= {movie.id}
        poster= {IMG_URL + movie.poster_path} 
        rating= {movie.vote_average} />
      ))}
      </div>

      <div className="trailer-container">       
      {trailerList.slice(0, 1).map(trailer => (
        <Trailer
        key= {trailer.key}
        trailer_key= {trailer.key}
      />
      ))}
      </div>



  </div>
  );
}

export default App;
