import React, { useEffect, useState } from 'react';
import axios from 'axios';

const IMG_URL_LQ = "https://image.tmdb.org/t/p/w500";
const API_KEY = "fb1090731e2fd7eea03536049722f122";


function MovieList({URL_PATH}) {
    const [movies, setMovies] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [isError, setIsError] = useState(false);
    useEffect(() => {
        const getData = async() => {
            setIsError(false);
            setIsLoading(true);

            try {
                const result = await axios(`https://api.themoviedb.org/3${URL_PATH}?api_key=${API_KEY}&language=en-GB&include_image_language=en,null`);
                setMovies(result.data.results);
                console.log("List Data: ", result.data.results)
            }
            catch (isError) {
                setIsError(true);
            }
            setIsLoading(false);
        }
        getData();
    }, [URL_PATH]);

    return (
        <div className="popular-movies">
        {isError && <div>Something went wrong...</div>}

        {isLoading ? (
        <div>Loading...</div>
        ) : (<div className="movie-container"> {movies.map(movie => (
                <div className="movie-list">
                <img src={IMG_URL_LQ + movie.poster_path} key={movie.id} movieid={movie.id} alt={movie.title} />
                </div>
            ))}</div>
            )}

        </div>
    )

}

export default MovieList
