import React, { useEffect, useState } from 'react';
import Movie from './getMovie';
import './App.css';

function App() {

const API_KEY = "fb1090731e2fd7eea03536049722f122";
const IMG_URL = "https://image.tmdb.org/t/p/w500";
//const BASIC_PATH = `https://api.themoviedb.org/3${path}?api_key=fb1090731e2fd7eea03536049722f122&language=en-GB`;

//const [mood, setMood] = useState('');


// movie list
const [movieList, setMovieList] = useState([]);

const path = "/movie/popular";

const happyMood = async () => {
  const response = await fetch(`https://api.themoviedb.org/3${path}?api_key=${API_KEY}&language=en-GB`);
  const data = await response.json();
  console.log("Movie List: ", data.results);
  setMovieList(data.results);
}


// CHECK FOR WHEN USER CLICKS IN THE IMAGE / GET MOVIE TRAILER 
document.onclick = function (event) { // check for any event that happens in the page
  var target = event.target; // store the content from the "event" into the variable target
  if (target.tagName.toLowerCase() === "img") { // change the image name to lower case and check if it is equal to "img"
    var movieId = target.attributes.movieid.value; // get movie ID
    console.log("Movie ID: ", movieId);
    var section = event.target.parentElement;
    var content = section.nextElementSibling; // add content after Section Element 

    // MOVIE TRAILER
    var path = `/movie/${movieId}/videos`; // create url to search for movie id
    var videoUrl = createUrl(path); // store the url in the varialbe trailerUrl

    // FETCH MOVIE VIDEOS
    fetch(videoUrl)
      .then((res) => res.json()) // return the value in json format 
      .then((data) => createVideoTemplate(data, content)) // send the content from "data" to the function createVideoTemplate(data, content)
      .catch((error) => { // check for error and display in the console
        console.log("Trailer Video Error: ", error);
      });
  }
}

return (
  <div className="App">
    <h1 className="logo"><a href="index.html">fastMovie</a></h1>

    <div className="mood-options">
      <p className="mood-status">How Are You Feeling Today?</p>
      <button onClick={() => happyMood()}>Happy</button>
      <button>Sad</button>
      <button>Tired</button>
      <button>Angry</button>
    </div>

      {movieList.map(movie => (
        <Movie 
        key= {movie.id}
        poster= {IMG_URL + movie.poster_path} 
        rating= {movie.vote_average} />
      ))}

  </div>
  );
}

export default App;
