import React from 'react';

const getMovie = ({poster, rating, movie_id}) => {
    return (
        <div> 
            <img src={poster} id={movie_id} alt=""/>
            <p>{rating}</p>
        </div>

    );
};

// CREATE YOUTUBE PLAYER
function createIframe(movie_id) {
    var iframe = document.createElement("iframe"); // create html element called iframe
    iframe.src = `https://www.youtube.com/embed/${movie_id}`; // Youtube dynamic key, will be updated everytime this fucntion is run
    iframe.width = 860;
    iframe.height = 515;
    iframe.frameborder = 0;
    iframe.allowFullscreen = true;
    return iframe;
  }

export default getMovie;
