import React, { useEffect, useState } from 'react';
import Trailer from './Trailer';
import Modal from 'react-modal';
import MovieList from './MovieList';
import RecommendedMovies from './RecommendedMovies';
import './App.css';

Modal.setAppElement('#root');

const API_KEY = "fb1090731e2fd7eea03536049722f122";
const IMG_URL_LQ = "https://image.tmdb.org/t/p/w500";
const IMG_URL_HQ = "https://image.tmdb.org/t/p/original";
const RECOMMENDED_MOVIES_PATH = "/discover/movie";
const UPCOMING_PATH = "/movie/upcoming";
const NOWPLAYING_PATH = "/movie/now_playing";
const POPULAR_PATH = "/movie/popular";


function App() {

const [trailerList, setTrailerList] = useState([]); // TRAILER LIST
const [showOverlay, setShowOverlay] = useState("overlay");
const [modalIsOpen, setmodalIsOpen] = useState(false);
const [genre, setGenre] = useState("");
const [randomYear, setRandomYear] = useState("");
const [isUserMoodActive, setIsUserMoodActive] = useState(false);
const [keyword1, setKeyword1] = useState("");
const [keyword2, setKeyword2] = useState("");
const [keyword3, setKeyword3] = useState("");
const [keyword4, setKeyword4] = useState("");
const [keyword5, setKeyword5] = useState("");

const [URL, setURL] = useState("");



const customStyles = {
  content: {
    top: '35%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    marginRight: '-50%',
    width: '40%',
    transform: 'translate(-50%, -10%)',
    textAlign: 'center',
  },
  overlay: {
    backgroundColor: 'rgb(0, 0, 0, .5)',
  },
};

let season;
let month;
let currentDate = new Date(); // variable to store date

// ARRAYS

// --- ANGRY Keywords ---
// fight 1721, martial arts 779, spy 470, tension 10481, warrior 192913, battle 14643, mercenary 3070, revenge 9748
const angryKeywords = ["1721","779","470","10481","192913","14643","3070","9748"];
// --- HAPPY Keywords ---
//happy 231591, love 9673, sports documentary 159290, nature documentary 221355, happiness 154060, family 18035, magic 2343
const happyKeywords = ["231591","9673","159290","221355","154060","18035","2343"];

// entertainer 1491, happy 231591, comedy 248206
const tiredKeywords = ["1491","231591","248206"];

// --- SPRING Keywords ---
// spring break 158054, game 18249, party 8508, young adult 206298
const springKeywords = ["158054","18249","8508","206298"];

// --- SUMMER Keywords ---
// summer 13088, summer camp 5767, beach 966, resort 10793, family secrets, teenage girl, family relationships
const summerKeywords = ["13088","5767","966","10793","6625","10809","10235"];

// --- WINTER Keywords ---
// winter 1442, snow 10794, christmas 207317, christmas party 1441, christmas horror 186466
const winterKeywords = ["1442","10794","207317","186466"];

// --- AUTUMN Keywords ---
// autumn 178883, halloween 3335, halloween party 9694, horror 8087, monster 1299, vampire 3133, supernatural 6152
const autumnKeywords = ["178883","3335","9694","8087","1299","3133","6152"];

// RANDOM Keywords Holder
// Array to store random keywords
// Used to merge two arrays together
let mergedKeywordsHolder = []; 

// Selected ramdom number from mergedKeywordsHolder array
let selectedKeywords = [];

// -------

// HAPPY USER MOOD OPTIONS
const happyMood = async () => {
    mergedKeywordsHolder = []; // clear the array
    selectedKeywords = []; // clear the array

  // CHECK IF IT IS SPRING
    if (getSeason() === "Spring") {
      console.log("It's Spring");
      mergedKeywordsHolder = happyKeywords.concat(springKeywords); // merge two arrays together into a new one called mergedKeywordsHolder
    }
    else if (getSeason() === "Summer") {
      console.log("It's Summer");
      mergedKeywordsHolder = happyKeywords.concat(summerKeywords); // merge two arrays together into a new one called mergedKeywordsHolder
    }
    else if (getSeason() === "Winter") {
      console.log("It's winter");
      mergedKeywordsHolder = happyKeywords.concat(winterKeywords); // merge two arrays together into a new one called mergedKeywordsHolder
    }
    else if (getSeason() === "Autumn") {
      console.log("It's Autumn");
      mergedKeywordsHolder = happyKeywords.concat(autumnKeywords); // merge two arrays together into a new one called mergedKeywordsHolder
    }
      // Call this fucntion to filter movies
      filterMovieKeywords();
}

// HAPPY USER MOOD OPTIONS
const tiredMood = async () => {

  setGenre("35|10770|53");
  mergedKeywordsHolder = []; // clear the array
  selectedKeywords = []; // clear the array

// CHECK IF IT IS SPRING
  if (getSeason() === "Spring") {
    console.log("It's Spring");
    mergedKeywordsHolder = tiredKeywords.concat(springKeywords); // merge two arrays together into a new one called mergedKeywordsHolder
  }
  else if (getSeason() === "Summer") {
    console.log("It's Summer");
    mergedKeywordsHolder = tiredKeywords.concat(summerKeywords); // merge two arrays together into a new one called mergedKeywordsHolder
  }
  else if (getSeason() === "Winter") {
    console.log("It's winter");
    mergedKeywordsHolder = tiredKeywords.concat(winterKeywords); // merge two arrays together into a new one called mergedKeywordsHolder
  }
  else if (getSeason() === "Autumn") {
    console.log("It's Autumn");
    mergedKeywordsHolder = tiredKeywords.concat(autumnKeywords); // merge two arrays together into a new one called mergedKeywordsHolder
  }
    // Call this fucntion to filter movies
    filterMovieKeywords();
}

// HAPPY USER MOOD OPTIONS
const angryMood = async () => {

  setGenre("28|80|10759|12");
  mergedKeywordsHolder = []; // clear the array
  selectedKeywords = []; // clear the array

// CHECK IF IT IS SPRING
  if (getSeason() === "Spring") {
    console.log("It's Spring");
    mergedKeywordsHolder = angryKeywords.concat(springKeywords); // merge two arrays together into a new one called mergedKeywordsHolder
  }
  else if (getSeason() === "Summer") {
    console.log("It's Summer");
    mergedKeywordsHolder = angryKeywords.concat(summerKeywords); // merge two arrays together into a new one called mergedKeywordsHolder
  }
  else if (getSeason() === "Winter") {
    console.log("It's winter");
    mergedKeywordsHolder = angryKeywords.concat(winterKeywords); // merge two arrays together into a new one called mergedKeywordsHolder
  }
  else if (getSeason() === "Autumn") {
    console.log("It's Autumn");
    mergedKeywordsHolder = angryKeywords.concat(autumnKeywords); // merge two arrays together into a new one called mergedKeywordsHolder
  }
  // Call this fucntion to filter movies
  filterMovieKeywords();
}


// FUNCTION TO FILTER ANGRY MOOD MOVIES
function filterMovieKeywords() {
  // loop to get 5 random values from array angryKeywords and store it in the array randomKeywordsHolder
  for (var i = 0; i < 5; i++) { 
    var pickedKeyword = mergedKeywordsHolder[Math.floor(Math.random()*mergedKeywordsHolder.length)]; // store the random value from the array angryKeywords into the variable pickedKeyword
    while (selectedKeywords.lastIndexOf(pickedKeyword) !== -1) { // check if the last value is not the same as the new one
      pickedKeyword = mergedKeywordsHolder[Math.floor(Math.random()*mergedKeywordsHolder.length)];
    }
    selectedKeywords.push(pickedKeyword); // adds the random value at the end of the array randomKeywordsHolder
}
  // assign keywords from randomKeywordsHolder array to variable keyword1, keyword2 etc
  setKeyword1(selectedKeywords[0]);
  setKeyword2(selectedKeywords[1]);
  setKeyword3(selectedKeywords[2]);
  setKeyword4(selectedKeywords[3]);
  setKeyword5(selectedKeywords[4]);

  // Generate random year between 2010 and 2020
  setRandomYear(getRndInteger(2010, 2020));
  console.log("selected keys ", selectedKeywords);
  console.log("keywords ",keyword1, keyword2, keyword3, keyword4,keyword5);

  console.log("URL TESTE ", URL);
}

function generateURL() {

  setURL(`https://api.themoviedb.org/3${RECOMMENDED_MOVIES_PATH}?api_key=${API_KEY}&language=en-GB&sort_by=popularity.desc&include_adult=false&include_video=false&primary_release_date.gte=${randomYear}-01-01&with_genres=${genre}&with_keywords=${keyword1}|${keyword2}|${keyword3}|${keyword4}|${keyword5}`);

}

// FUNCTION TO GENERATE RANDOM YEAR
